import React from "react";
import AddUserForm from "../../FormComponent/AirportForm/AddAirport";
import { useToasts } from "react-toast-notifications";
import LoginAPI from "../../API/Login/login";

const CreateAirports = () => {
  const { addToast } = useToasts();

  const submitProfile = async (values, setSubmitting) => {
    try {
      const resp = await LoginAPI(values);

      if (resp) {
        const { jwt, refresh_token } = resp;

        localStorage.setItem("bearerToken", jwt);
        localStorage.setItem("refreshToken", refresh_token);

        setLoggedIn(true);

        history.push("/");

        addToast("Logged In Successfully", {
          appearance: "success",
          autoDismiss: 500,
        });
      }

      setSubmitting(false);
    } catch (e) {
      setSubmitting(false);
      addToast("Error While Logged In", {
        appearance: "error",
        autoDismiss: 500,
      });
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          backgroundColor: "#FFFFFF",
          minWidth: "400px",
          maxWidth: "400px",
          marginTop: "40px",
        }}
      >
        <h3
          style={{
            textAlign: "center",
            marginTop: "2vh",
            marginBottom: "3vh",
          }}
        >
          CREATE AIRPORTS
        </h3>
        <AddUserForm />
      </div>
    </div>
  );
};
export default CreateAirports;
