import React from "react";
import LoginForm from "../FormComponent/LoginForm/LoginForm";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Avatar from "@material-ui/core/Avatar";
import { useToasts } from "react-toast-notifications";
import { useHistory } from "react-router-dom";
import LoginAPI from "../../API/Login/login";
import { BlueButton } from "../../UI/customButton";
import { useAuth } from "../../context/AuthContext";

const Login = () => {
  const { addToast } = useToasts();
  const { setLoggedIn } = useAuth();
  const history = useHistory();

  const submitProfile = async (values, setSubmitting) => {
    try {
      const resp = await LoginAPI(values);

      if (resp) {
        const { jwt, refresh_token } = resp;

        localStorage.setItem("bearerToken", jwt);
        localStorage.setItem("refreshToken", refresh_token);

        setLoggedIn(true);

        history.push("/");

        addToast("Logged In Successfully", {
          appearance: "success",
          autoDismiss: 500,
        });
      }

      setSubmitting(false);
    } catch (e) {
      setSubmitting(false);
      addToast("Error While Logged In", {
        appearance: "error",
        autoDismiss: 500,
      });
    }
  };

  const redirectToSignUp = () => {
    history.push("/sign-up");
  };

  return (
    <Container component="main" maxWidth="xs">
      <Avatar className={classes.avatar}>
        <LockOutlinedIcon />
      </Avatar>
      <h1
        style={{ textAlign: "center", marginTop: "10vh", marginBottom: "3vh" }}
      >
        Login
      </h1>
      <LoginForm onSubmit={submitProfile} />

      <Grid
        container
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Grid item>{"Don't have an account? Sign Up"}</Grid>
      </Grid>
    </Container>
  );
};
export default Login;
