import React from "react";
import LoginForm from "../FormComponent/LoginForm/LoginForm";
import { useToasts } from "react-toast-notifications";
import { useHistory } from "react-router-dom";
import LoginAPI from "../../API/Login/login";
import { BlueButton } from "../../UI/customButton";
import { useAuth } from "../../context/AuthContext";
const Login = () => {
  const { addToast } = useToasts();
  const { setCurrentUser } = useAuth();
  const history = useHistory();

  const submitProfile = async (values, setSubmitting) => {
    try {
      const resp = await LoginAPI(values);

      if (resp) {
        const { jwt, refresh_token } = resp;

        localStorage.setItem("bearerToken", jwt);
        localStorage.setItem("refreshToken", refresh_token);

        addToast("Logged In Successfully", {
          appearance: "success",
          autoDismiss: 500,
        });

        history.push("/");
      }

      setSubmitting(false);
    } catch (e) {
      setSubmitting(false);
      addToast("Error While Logged In", {
        appearance: "error",
        autoDismiss: 500,
      });
    }
  };

  const redirectToSignUp = () => {
    history.push("/sign-up");
  };

  return (
    <>
      <h1
        style={{ textAlign: "center", marginTop: "10vh", marginBottom: "7vh" }}
      >
        Login
      </h1>
      <LoginForm onSubmit={submitProfile} />

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <h5>Not Sign Up yet ?</h5>
        <BlueButton onClick={redirectToSignUp} style={{ margin: "7px" }}>
          Sign Up
        </BlueButton>
      </div>
    </>
  );
};
export default Login;
